lns=\"http://www.w3.org/1999/xhtml\">\n\n<head>\n  <meta http-equiv=\"Content-Type\" content=\"text/html; charset=utf-8\" />\n  <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\" />\n  <title>{{ company_name }}: Message title</title>\n  <style type=\"text/css\">\n    #outlook a {\n      padding: 0;\n    }\n\n    body {\n      width: 100% !important;\n      -webkit-text-size-adjust: 100%;\n      -ms-text-size-adjust: 100%;\n      margin: 0;\n      padding: 0;\n    }\n\n    img {\n      outline: none;\n      text-decoration: none;\n      -ms-interpolation-mode: bicubic;\n    }\n\n    a {\n      outline: none;\n    }\n\n    a img {\n      border: none;\n    }\n\n    .image_fix {\n      display: block;\n    }\n\n    .message-header > td {\n      padding: 10px 30px 20px 30px;\n    }\n\n    .message-header__title {\n      background-color: {% if styles.links %}{{styles.links}}{% else %}#999{% endif %}\n    }\n\n    .message-header__title > td {\n      padding: 20px 30px;\n    }\n\n    .message-header__title h1 {\n      font-size: 20px;\n      text-transform: uppercase;\n      font-weight: normal;\n      color: #Fff;\n    }\n\n    .message-title > td,\n    .message-body > td {\n      padding: 30px;\n    }\n\n    .message-footer > td {\n      padding: 20px 30px;\n      background-color: #757f83;\n    }\n\n    .message-copyright > td {\n      padding: 0px 30px 10px;\n    }\n\n    .message-header td,\n    .message-title td,\n    .message-body th, .message-body td,\n    .message-footer th, .message-footer td,\n    .message-copyright th, .message-copyright td {\n      color: {{styles.font}};\n      font-size: {{styles.body_font_size}};\n      font-family: {{styles.body_font}},Helvetica,Arial,sans-serif;\n    }\n\n    .message-footer {\n      border-top: 1px solid {{styles.base}}\n    }\n\n    .message-body table th,\n    .message-footer table th {\n      text-transform: uppercase;\n      border-bottom: 1px solid {{styles.base}};\n      text-align: left;\n    }\n\n    .message-body table td,\n    .message-footer table td {\n      padding: 5px;\n    }\n\n    .message-footer table th{\n        border: none;\n    }\n\n    .message-footer td {\n      color: #fff;\n    }\n\n    .footer-contact__title {\n      margin: 0px;\n      text-transform: uppercase;\n      font-size: 16px !important;\n      font-weight: bold;\n      color: #fff !important;\n    }\n\n    .footer-social__title {\n      margin: 0px;\n      text-transform: uppercase;\n      font-size: 16px !important;\n      font-weight: bold;\n      color: #fff !important;\n    }\n\n    .message-footer table td.footer-social td {\n      padding: 0px;\n      padding-right: 10px;\n    }\n\n    .email-preview{\n      display:none;\n      font-size:1px;\n      color:#333333;\n      line-height:1px;\n      max-height:0px;\n      max-width:0px;\n      opacity:0;\n      overflow:hidden;\n    }\n\n    .with-subline {\n      color: {{styles.font}};\n      text-transform: uppercase;\n      font-weight: bold;\n      font-size: 1em;\n      padding-bottom: 10px;\n      border-bottom: 1px solid #D4D4D4;\n    }\n\n    p {\n      margin: 1em 0;\n    }\n\n    h1,h2,h3,h4,h5,h6 {\n      color: {{styles.font}};\n    }\n\n    h1 a,h2 a,h3 a,h4 a,h5 a,h6 a {\n      color: {{styles.links}};\n    }\n\n    h1 a:active,h2 a:active,h3 a:active,h4 a:active,h5 a:active,h6 a:active {\n      color: red;\n    }\n\n    h1 a:visited,h2 a:visited,h3 a:visited,h4 a:visited,h5 a:visited,h6 a:visited {\n      color: purple;\n    }\n\n    table td,\n    table th {\n      border-collapse: collapse;\n    }\n\n    table {\n      border-collapse: collapse;\n      mso-table-lspace: 0pt;\n      mso-table-rspace: 0pt;\n    }\n\n    address {\n      margin: 0px;\n    }\n\n    .content-wrapper {\n      border: 1px solid {{ styles.base }};\n      background-color: {{ styles.general_bg_color }};\n    }\n\n    .copyright td {\n      padding: 10px 0 0 0;\n      padding-bottom: 0 !important;\n    }\n\n    a {\n      color: {{styles.links}};\n    }\n\n    {% if language_direction == \"rtl\" %}\n\n    .ty-email-footer {\n      text-align: center !important;\n    }\n\n    .ty-email-footer-social-buttons {\n      text-align: center !important;\n    }\n\n    .ty-email-footer-right-part {\n      text-align: center !important;\n      float: left !important;\n    }\n\n    .ty-email-footer-left-part {\n      text-align: center !important;\n      float: right !important;\n    }\n\n    {% endif %}\n\n  </style>\n</head>\n\n<body>\n    <style>\n\n      @media only screen and (max-device-width: 480px){\n        .content-wrapper{width: 100% !important;border: 3px solid #ccc !important; }\n\n        table[width]{\n          width: 100%! important;\n        }\n\n        .message-header > td,\n        .message-title > td,\n        .message-body > td,\n        .message-footer > td,\n        .message-copyright > td {\n          padding: 10px !important;\n        }\n\n        .message-header td,\n        .message-title td,\n        .message-body th, .message-body td,\n        .message-footer th, .message-footer td,\n        .message-copyright th, .message-copyright td {\n          font-size: 16px !important;\n        }\n      }\n\n      @media only screen and (min-device-width: 768px) and (max-device-width: 1024px) {}\n\n      @media only screen and (-webkit-min-device-pixel-ratio: 2) {}\n\n      @media only screen and (-webkit-device-pixel-ratio:.75) {}\n\n      @media only screen and (-webkit-device-pixel-ratio:1) {}\n\n      @media only screen and (-webkit-device-pixel-ratio:1.5) {}\n    </style>\n  <!-- Targeting Windows Mobile -->\n  <!--[if IEMobile 7]>\n  <style type=\"text/css\">\n\n  </style>\n  <![endif]-->\n\n  <!--[if gte mso 9]>\n    <style>\n        /* Target Outlook 2007 and 2010 */\n    </style>\n  <![endif]-->\n  <table class=\"main-wrapper\" cellpadding=\"0\" cellspacing=\"0\" width=\"100%\" bgcolor=\"{{ styles.base }}\" style=\"background-color: {{ styles.base }}\" dir=\"{{ language_direction }}\">\n    <tr>\n      <td style=\"padding: 40px 10px 40px 10px;\">\n        <table class=\"content-wrapper\" cellpadding=\"0\" cellspacing=\"0\" align=\"center\" width=\"600\">\n          <tr class=\"message-header\">\n            <td>\n              <table cellpadding=\"0\" cellspacing=\"0\" width=\"100%\">\n                <tr>\n                  <td><a href=\"{{ storefront_data.url }}\"><img src=\"{{logos.mail.image.image_path}}\" alt=\"{{ company_data.company_name }}\" width=\"{{logos.mail.image.image_x}}\" height=\"{{logos.mail.image.image_y}}\" style=\"width: auto; max-width: 300px; max-height: 150px;\" /></a></td>\n                </tr>\n              </table>\n            </td>\n          </tr>\n          {% if title %}\n          <tr class=\"message-header__title\">\n            <td>\n              <h1>{{title}}</h1>\n            </td>\n          </tr>\n          {% endif %}\n\n          <tr class=\"message-body\">\n\n<td>','A',NULL,NULL,'',1659580138,1659580138),(3,'bill_to','order_invoice',NULL,'\n        {% if u.b_firstname or u.b_lastname or u.b_address or u.b_address_2 or u.b_city or u.b_state_descr or u.b_zipcode %}\n        <h2 style=\"margin: 0px; font-size: 22px; font-family: Helvetica, Arial, sans-serif; color: #444444; text-transform: uppercase; padding-bottom: 20px; border-bottom: 3px solid #e8e8e8; margin-bottom: 20px;\">{{__(\"bill_to\")}}</h2>\n        <p style=\"margin: 0px; padding-bottom: 5px; font-family: Helvetica, Arial, sans-serif;\">\n            <strong>{{u.b_firstname}} {{u.b_lastname}}</strong>\n        </p>\n        <p style=\"margin: 0px; padding-bottom: 5px; font-family: Helvetica, Arial, sans-serif;\">\n            {{u.b_address}} <br>{{u.b_address_2}}\n        </p>\n        <p style=\"margin: 0px; padding-bottom: 5px; font-family: Helvetica, Arial, sans-serif;\">\n            {{u.b_city}}, {{u.b_state_descr}} {{u.b_zipcode}}\n        </p>\n        <p style=\"margin: 0px; padding-bottom: 5px; font-family: Helvetica